use clap::{Arg, ArgAction};
use clap_complete::ArgValueCompleter;

fn worker_completer(current: &std::ffi::OsStr) -> Vec<clap_complete::CompletionCandidate> {
    // Load worker IDs from config
    let workers = load_worker_ids().unwrap_or_default();
    workers
        .into_iter()
        .filter(|w| w.starts_with(&current.to_string_lossy().as_ref()))
        .map(|w| clap_complete::CompletionCandidate::new(w))
        .collect()
}

#[derive(Parser)]
struct Cli {
    #[command(subcommand)]
    command: Commands,
}

#[derive(Subcommand)]
enum Commands {
    Workers {
        #[command(subcommand)]
        action: WorkerAction,
    },
}

#[derive(Subcommand)]
enum WorkerAction {
    Probe {
        #[arg(add = ArgValueCompleter::new(worker_completer))]
        worker: Option<String>,
    },
}
